import { useState } from 'react';
import { Menu, Button, MenuItem } from '@material-ui/core';

const monthes = [
    'Janurary',
    '12:15am',
    'Callisto',
    'Dione',
    'Ganymede',
    'Hangouts Call',
    'Luna',
    'Oberon',
    'Phobos',
    'Pyxis',
    'Sedna',
    'Titania',
    'Triton',
    'Umbriel',
];

const dates = [
    "00",
    "01"
]

const years = [
    "2020",
    "2021",
    "2022",
    "2023"
] //don't have to add more

const DateSelector = ({ name }) => {
    const [monthAnchor, setMonthAnchor] = useState(null);
    const [dateAnchor, setDateAnchor] = useState(null);
    const [yearAnchor, setYearAnchor] = useState(null);
    const [m, setMonth] = useState("Month");
    const [d, setDate] = useState("Date");
    const [y, setYear] = useState("Year");

    //////////
    const handleMClick = (event) => {
        setMonthAnchor(event.currentTarget);
    };
    const handleMonthClick = (event) => {
        setMonth(event.target.textContent);
        handleMClose();
    };
    const handleMClose = () => {
        setMonthAnchor(null);
    };
    ////////////////
    const handleDClick = (event) => {
        setDateAnchor(event.currentTarget);
    };
    const handleDayClick = (event) => {
        setDate(event.target.textContent);
        handleDClose();
    };
    const handleDClose = () => {
        setDateAnchor(null);
    };
    //////////////////
    const handleYClick = (event) => {
        setYearAnchor(event.currentTarget);
    };
    const handleYearClick = (event) => {
        setYear(event.target.textContent);
        handleYClose();
    };
    const handleYClose = () => {
        setYearAnchor(null);
    };

    return (
        <>
            <Button children={m} aria-controls="simple-menu" aria-haspopup="true" onClick={handleMClick} />
            <Menu
                id="simple-menu"
                anchorEl={monthAnchor}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
                PaperProps={{ style: { maxHeight: 200 } }}
            >
                {monthes.map((month) => (
                    <MenuItem children={month} onClick={handleMonthClick} />
                ))}

            </Menu>
            <Button children={d} aria-controls="simple-menu" aria-haspopup="true" onClick={handleDClick} />
            <Menu
                id="simple-menu"
                anchorEl={dateAnchor}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
                PaperProps={{ style: { maxHeight: 200 } }}
            >
                {dates.map((date) => (
                    <MenuItem children={date} onClick={handleDateClick} />
                ))}

            </Menu>
            <Button children={y} aria-controls="simple-menu" aria-haspopup="true" onClick={handleYClick} />
            <Menu
                id="simple-menu"
                anchorEl={yearAnchor}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
                PaperProps={{ style: { maxHeight: 200 } }}
            >
                {years.map((year) => (
                    <MenuItem children={year} onClick={handleYearClick} />
                ))}

            </Menu>
        </>
    );
}

export default DateSelector;
