import { useState, useEffect } from 'react';
import { Paper, TextField, Grid, Button } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';

import useStyles from './styles';
import TimeMenu from './TimeMenu/TimeMenu';
import DateSelector from './DateSelector/DateSelector'

import { createEvent, updateEvent } from '../../actions/events';

function SubmitForm() {
    const [eventData, setEventData] = useState({ creator: '', title: '', message: '', tags: '', selectedFile: '' });
    const event = useSelector((state) => (currentId ? state.events.find((message) => message._id === currentId) : null));
    const dispatch = useDispatch();

    const classes = useStyles();
    let timeName1 = "Start Time";
    let timeName2 = "End Time";
    let date = "";

    useEffect(() => {
        if (post) setPostData(post);
    }, [post]);


    const callbackFunction = (time) => {
        timeName1 = time;
    }
    const callbackFunction2 = (time) => {
        timeName2 = time;
    }
    const callbackFunction3 = (month, day, year) => {
        date = month + day + year;
    }

    return (
        <Paper className={ classes.paper }>
            <Grid container direction="column" justify="center" alignItems="center" className={ classes.grid }>
                <Grid className={ classes.title }>
                    <TextField id="SubmitFormTitle" variant="outlined" label="Title" />
                </Grid>
                <Grid item className={classes.time} >
                    <DateSelector callBack={ callbackFunction3 }/>
                </Grid>
                <Grid item className={classes.description} >
                    <TextField id="SubmitFormDescription" multiline variant="outlined" label="Description" />
                </Grid>
                <Grid item className={classes.time} >
                    <TimeMenu name="Start Time" callBack={callbackFunction} />
                     - 
                    <TimeMenu name="End Time" callBack={callbackFunction2}/>
                </Grid>
                <Grid item className={classes.time} >
                    <Button className={ classes.button } children="Cancel" />
                    <Button className={ classes.button } children="Sumbit" />
                </Grid>
            </Grid>
            
        </Paper>
    );
}

export default SubmitForm;
