import React, { } from 'react';
import { } from '@material-ui/core';

class InputBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            major: '',
        };
        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangeMajor = this.handleChangeMajor.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        //this.setName = this.setName.bind(this); 


    }

    handleChangeName(event) {
        this.setState({ name: event.target.value })
    }

    handleChangeEmail(event) {
        this.setState({ email: event.target.value })
    }

    handleChangeMajor(event) {
        this.setState({ major: event.target.value })
    }

    handleSubmit(event) {
        alert(this.state.name + " was added");
        event.preventDefault();
    }

    /*setName(event){
        event.preventDefault();
        const name = this.state.name;
        this.props.onSearchTermChange(name);
        this.setState({
           name: ''
         });
         
    }*/

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                </label>

                <label>
                    <p>Name: </p>
                    <input type="text" value={this.state.name} onChange={this.handleChangeName} />

                </label>

                <label>
                    <p>Email: </p>
                    <input type="text" value={this.state.email} onChange={this.handleChangeEmail} />
                </label>

                <label>
                    <p>Major: </p>
                    <input type="text" value={this.state.major} onChange={this.handleChangeMajor} />
                </label>

                <p><button type="enter">submit</button></p>

            </form>
        );
    }
}

export default InputBox;
